CREATE TABLE IF NOT EXISTS`campaigns` (`id` int(1) NOT NULL AUTO_INCREMENT, `name` varchar(255) DEFAULT NULL, `description` varchar(255) DEFAULT NULL, `email` varchar(255) DEFAULT NULL, PRIMARY KEY (`id`)) ENGINE=InnoDB AUTO_INCREMENT=25 DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `users` (`id` int(1) NOT NULL AUTO_INCREMENT,`email` varchar(255) NOT NULL,`password` varchar(255) DEFAULT NULL,`firstname` varchar(255) DEFAULT NULL,`lastname` varchar(255) DEFAULT NULL,`companyName` varchar(255) DEFAULT NULL,`jobTitle` varchar(255) DEFAULT NULL,`plan` varchar(20) DEFAULT NULL,`cardNumber` varchar(255) DEFAULT NULL,`cardDate` varchar(20) DEFAULT NULL,`cvv` varchar(20) DEFAULT NULL,`cardHolder` varchar(255) DEFAULT NULL,`postalCode` varchar(20) DEFAULT NULL,`userActive` tinyint(1) DEFAULT 0,PRIMARY KEY (`id`,`email`)) ENGINE=InnoDB AUTO_INCREMENT=19 DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `workflowwebsites` (`id` int(1) NOT NULL AUTO_INCREMENT,`email` varchar(255) DEFAULT NULL,`cookieValue1` varchar(255) DEFAULT NULL,`cookieValue2` varchar(255) DEFAULT NULL,`cookieValue3` varchar(255) DEFAULT NULL,`companyNumber` int(20) DEFAULT NULL,`companyMapping` varchar(20) DEFAULT NULL,`launchSchedule` varchar(20) DEFAULT NULL,`notification` varchar(20) DEFAULT NULL,`cvFile` varchar(255) DEFAULT NULL,`fileName` varchar(20) DEFAULT NULL,`update_date` varchar(20) DEFAULT NULL,PRIMARY KEY (`id`)) ENGINE=InnoDB AUTO_INCREMENT=14 DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `usersconfigs` (`id` int(1) NOT NULL AUTO_INCREMENT,`email` varchar(255) DEFAULT NULL,`plan` varchar(255) DEFAULT NULL,`last_time_charged` DATETIME DEFAULT NULL,`free_trial_status` tinyint(0) DEFAULT NULL, PRIMARY KEY (`id`)) ENGINE=InnoDB AUTO_INCREMENT=14 DEFAULT CHARSET=utf8;